rule.definition:
#"SYSTEM_SYSRULES_gridRowLayout"(
  id: ri!index,
  contents: {
    if(
      ri!index > ri!oldAttachments,
      #"SYSTEM_SYSRULES_fileUploadField"(
        label: "file upload " & ri!index,
        target: ri!target,
        value: ri!attachment,
        saveInto: ri!attachment
      ),
      #"SYSTEM_SYSRULES_linkField"(
        label: "document download link " & ri!index,
        links: #"SYSTEM_SYSRULES_documentDownloadLink"(
          document: ri!attachment,
          label: #"_a-0000e48b-4cb4-8000-9baf-011c48011c48_3412300"(ri!attachment)
        )
      )
    ),
    if(
      ri!index > ri!oldAttachments,
      #"SYSTEM_SYSRULES_textField"(
        label: "uploaded " & ri!index,
        readOnly: true,
        value: ""
      ),
      #"SYSTEM_SYSRULES_textField"(
        label: "uploaded " & ri!index,
        readOnly: true,
        align: "RIGHT",
        value: #"_a-0000e48b-4cb4-8000-9baf-011c48011c48_3412304"(document(ri!attachment, "dateCreated"))
      )
    ),
    if(
      ri!readOnly,
      {},
      #"SYSTEM_SYSRULES_imageField_v1"(
        label: "delete " & ri!index,
        images: #"SYSTEM_SYSRULES_documentImage"(
          document: #"SYSTEM_SYSRULES_iconIndicator"("REMOVE"),
          altText: "Remove",
          caption: "Remove",
          link: #"SYSTEM_SYSRULES_dynamicLink"(
            value: ri!index,
            saveInto: {
              if(
                isnull(ri!attachment),
                {},
                a!save(ri!deletedAttachments, append(ri!deletedAttachments, ri!attachment))
              ),
              a!save(ri!attachments, remove(ri!attachments, save!value)),
              if(
                ri!index > ri!oldAttachments,
                {},
                a!save(ri!oldAttachments, ri!oldAttachments - 1)
              )
            }
          )
        ),
        size: "ICON"
      )
    )
  }
)
