rule.definition:
#"SYSTEM_SYSRULES_richTextDisplayField"(
  label: "Filters",
  labelPosition: "ADJACENT",
  value: {
    #"SYSTEM_SYSRULES_richTextItem_v1"(
      text: "Creator: " & #"b0d765bb-bdab-4639-be58-faa1a56c2522_FORMATNAME"(ri!creator) & char(10),
      showWhen: not(isnull(ri!creator))
    ),
    #"SYSTEM_SYSRULES_richTextItem_v1"(
      text: "Created In: " & text(eomonth(today(),ri!creationTime), "YYYY-M") & char(10),
      showWhen: not(isnull(ri!creationTime))
    ),
    #"SYSTEM_SYSRULES_richTextItem_v1"(
      text: "Status: " & ri!status & char(10),
      showWhen: not(isnull(ri!status))
    ),
    #"SYSTEM_SYSRULES_richTextItem_v1"(
      text: "Priority: " & ri!priority & char(10),
      showWhen: not(isnull(ri!priority))
    ),
    if(
      all(fn!isnull, {ri!creator, ri!creationTime, ri!status, ri!priority, ri!searchTerm}),
      #"SYSTEM_SYSRULES_richTextItem_v1"(text: "<no filter applied>", style: "EMPHASIS"),
      {
        #"SYSTEM_SYSRULES_richTextItem_v1"(
          text: "(Clear Filters and Search)",
          link: #"SYSTEM_SYSRULES_dynamicLink"(
            value: null,
            saveInto: {
              ri!creator,
              ri!creationTime,
              ri!status,
              ri!priority,
              ri!searchTerm,
              a!save(ri!pagingInfo.startIndex, 1)
            }
          )
        )
      }
    )
  }
)
